import { useEffect, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import Header from "../../components/Header";
import { getUser } from "../../services/security";
import { api } from "../../services/api";
import {Container, FormNewPost} from "./style";
function NewImage() {
    const history = useHistory();


    const [isLoading, setIsLoading] = useState(false);

    const imageRef = useRef();
    console.log(imageRef.height)
    const [image, setImage] = useState(null);

    const handleImage = (e) => {
        if (e.target.files[0]) {
            imageRef.current.src = URL.createObjectURL(e.target.files[0]);
            imageRef.current.style.display = "flex";
        } else {
            imageRef.current.src = "";
            imageRef.current.style.display = "none";
        }

        setImage(e.target.files[0]);
    };

    const handleSubmit = async (e) => {

        console.log("sim")
        e.preventDefault();

        const data = new FormData();


        image && data.append("image", image);
        console.log(data);
        setIsLoading(true);
        const Student = getUser();
        console.log(image.widths)
        try {
            await api.post("/students/"+Student.studentId+"/images", data, {
                headers: {
                    "Content-type": "multipart/form-data"
                }
            });
            alert('Imagem atualizada com sucesso!');

           // history.goBack();
        } catch (error) {
            console.error(error);
            alert(error);
        } finally{
            setIsLoading(false);
        }
    }

    // if(isLoading)
    //     return <div>Carregando...</div>

    return (
        <Container>
            <Header />
            <FormNewPost onSubmit={handleSubmit}>
                <h1>Nova Imagem</h1>

                <input type="file" accept="image/*" onChange={handleImage} />
                <img alt="Pré-visualização" ref={imageRef} />
                <button>Enviar</button>
            </FormNewPost>
        </Container>
    );
}

export default NewImage;
