import { CardComent, CardPost } from "./styles";
import imgProfile from "../../assets/profile.png"
import { useState } from "react";
import { getUser } from "../../services/security";
import { format } from "date-fns";
import {api} from "../../services/api";

function Post({ data }) {

    let signedUser = getUser();

    const [newComent, setComents]  = useState({
        description: "",
    });

    const handleComents = (e) => {
        setComent( previusState => ({...previusState, [event.target.id]: event.target.value}))

    }
    const [showComents, setShowComents] = useState(false);

    const toggleComents = () => setShowComents(!showComents);

    return (
        <CardPost>
            <header>
                <img src={imgProfile} />
                <div>
                    <p>por {signedUser.studentId === data.Student.id ? "você" : data.Student.name}</p>
                    <span>em {format(new Date(data.created_at), "dd/MM/yyyy 'às' HH:mm")}</span>
                </div>
            </header>
            <main>
                <div>
                    <h2>{data.title}</h2>
                    <p>{data.description}</p>
                </div>
                {data.image && <img src={data.image} alt="imagem do post" />}
                <footer>
                    {data.Categories.map(c => <p>{c.description}</p>)}
                </footer>
            </main>
            <footer>
                <h3 onClick={toggleComents}>
                    {
                        data.Answers.length === 0 ?
                            "Seja o primeiro a comentar" :
                            `${data.Answers.length} Comentário${data.Answers.length > 1 && "s"}`
                    }
                </h3>
                {showComents && (
                    <>
                        { data.Answers.map(newComent => <Coment newComent={newComent} />) }
                    </>
                )}
                <div>
                    <input placeholder="Comente este post" onChange={handleComents} />
                    <button onClick={() => add(data,newComent,setComents)}>Enviar</button>
                </div>
            </footer>
        </CardPost>
    );
}

function Coment({newComent}) {

    return (
        <CardComent>
            <header>
                <img src={imgProfile} />
                <div>
                    <p>por Ciclano</p>
                    <span>em 10/10/2021 às 13:00</span>
                </div>
            </header>
            <p>Este é o comentário</p>
        </CardComent>
    );
}
async function add(data,newComent,setComents){

    const payload = {
        description : newComent
    };

    console.log(payload)
    try {
        const response = await api.post(`/questions/${data.id}/answers`, payload, {
            headers: {
                "Content-type": "application/json"
            }
        });
        console.log(response);
        alert("comentario inserido com sucesso");
        const newAnwser = {
            id: response.data.id,
            description: response.data.description,
            created_at: response.data.createdAt,
            Student: getUser()
        }
        data.Answers.push(newAnwser);
        setComents({description: ""});


    } catch (error) {
        console.error(error);
        alert(error);
    }
}

export default Post;
